//Handles all the typing game logic

import React from 'react';
import axios from 'axios';

import Form from 'react-bootstrap/Form';
import Spinner from 'react-bootstrap/Spinner';

function ShowScore(props) {
  if (!props.won.complete) {
    return (
      <div>Not done</div>
    );
  }

  return (
    <div>Done</div>
  );
}


class TypingGame extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      fullText: [],
      displayText: [],
      wordCount: 0,
      score: 0,
      wordCounter: 0,
      textCounter: 0,
      input: "",
      complete: false,
      startTime: 0,
      endTime: 0,
      loading: false
    };
  }

  componentDidMount() {
    this.setState({
      loading: true
    });
    this.FetchData();
  }
  
  FetchData = () => {
    axios.get('https://en.wikipedia.org/api/rest_v1/page/random/summary')
    .then(res => {
      const data = res.data.extract;

      // Sanitizing text by removing accents/diacritics
      const noSpacesData = data.replace(/\s\s+/g, ' ');
      const cleanData = noSpacesData.normalize("NFD").replace(/[\u0300-\u036f]/g, "");

      //Get total number of letters for Words per minute calculation at the end
      const wordCount = cleanData.length;
      
      //Recursively fetch data until conditions are met
      if (wordCount < 300 || wordCount > 500) {
        return this.FetchData();
      }

      // Split text by sentence
      let splitData = cleanData.split(". ");

      //Request data debugging
      console.log(wordCount);
      console.log(splitData);

      this.setState(() => ({
        //One state variable for the full text and one for the first sentence
        fullText: splitData,
        displayText: splitData[0].split(" "),
        wordCount: wordCount,
        loading: false
      }));
    })
  }
  
  TimeLog = () => {
    console.log(this.state.endTime);
  }

  StartTimer = () => {
    if (this.state.startTime === 0) {
      this.setState({
        startTime: new Date().getTime()
        }, this.TimeLog() );  
    }
  }
  
  FinishTimer = () => {
    this.setState(state => ({
      endTime: ((new Date().getTime()) - state.startTime)
    }), console.log(this.state.endTime) );
  }

  updateResponse = (event) => {
    this.setState({
      input: event.target.value
    })
  }

  nextWord = (event) => {
    //Starts the timer to calculate the score in the end
    this.StartTimer();
    if (event.code === 'Space') {
      const word = this.state.input;
      
      if (word.replace(" ", "") === this.state.displayText[this.state.wordCounter]) {
        this.setState({
          score: this.state.score + 1,
          input: "",
          wordCounter: this.state.wordCounter + 1
        });
      } else {
        this.setState({
          input: ""
        });
      }
      //check if we are done with the current text
      if (this.state.wordCounter + 1 === this.state.displayText.length) {
        //check if we are done with the whole text
        if (this.state.textCounter + 1 === this.state.fullText.length) {
          console.log("YOU WON!");
          this.setState(state => ({
            complete: !state.complete,
          }), this.FinishTimer());
        } else {
          this.setState(state => ({
            displayText: state.fullText[state.textCounter + 1].split(" "),
            textCounter: state.textCounter + 1,
            wordCounter: 0
          }));
        } 
      }
    }
  }
  
  render() {
    return (
      <div className="typing-test">
        
        {
          this.state.loading
          
          ? <Spinner id="gameSpinner" animation="border" variant="info" />
          
          : <div id="text-area">
              {this.state.displayText.map((text) =>
                <span className="test-displayText">{text} </span>
              )}
            </div>
        }
        
        <div className="gap-20"></div>
        <Form.Control onKeyPress={this.nextWord} onChange={this.updateResponse} value={this.state.input} className="form"/>
        <div className="gap-20"></div>
        
        {/* Debugging info */}
        <div>Input: {this.state.input}</div>
        <div>Score: {this.state.score}</div>
        <div>Counter: {this.state.wordCounter}</div>
        <div>Current word: {this.state.displayText[this.state.wordCounter]}</div>
        
        <ShowScore won={this.state} />
        
        <button onClick={this.TimeLog}>Show Result</button>
        
      </div>
    );
  }

}

export default TypingGame;
