import React from 'react';

import TypingGame from './TypingGame';
import Score from '../components/GameComplete';

class GameArea extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      phase: 0
    };
  }
  
  componentDidMount() {
    this.setState(state => ({
      phase: state.phase + 1
    }));
  }
  
  render() {
    return (
      <div>
        <TypingGame />
      </div>
    )
  }
}

export default GameArea;
