import React from 'react';
import axios from 'axios';

import Form from 'react-bootstrap/Form';

class TypingGame extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      words: [],
      score: 0,
      counter: 0,
      input: ""
    };
  }

  componentDidMount() {
    axios.get('https://en.wikipedia.org/api/rest_v1/page/random/summary')
    .then(res => {
      const data = res.data.extract;
      let splitData = data.split(" ");
      console.log(splitData);
      this.setState(() => ({
        words: splitData
      }));
    })
  }
  
  updateResponse = (event) => {
    this.setState({
      input: event.target.value
    })
  }

  nextWord = (event) => {
    if (event.code === 'Space') {
      const word = this.state.input;
      if (word.replace(" ", "") === this.state.words[0]) {
        this.setState({
          score: this.state.score + 1,
          input: "",
          counter: this.state.counter + 1
        })  
      } else {
        this.setState({
          input: ""
        })
      }
    }
  }
  
  render() {
    return (
      <div className="typing-test">
        <div>{this.state.words.map((word) =>
          <span className="test-words">{word}&nbsp;</span>
        )}
        </div>
        <div className="gap-20"></div>
        <Form.Control onKeyPress={this.nextWord} onChange={this.updateResponse} value={this.state.input} className="form"/>
        <div className="gap-20"></div>
        
        <div>Input: {this.state.input}</div>
        <div>Score: {this.state.score}</div>
        <div>Counter: {this.state.counter}</div>
        <div>Current word: {this.state.words[0]}</div>
        
      </div>
    );
  }

}

export default TypingGame;
