import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';

import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';

import * as actions from '../store/actions/auth';

class SignupForm extends React.Component {

  handleFormSubmit = (event) => {
    event.preventDefault();
    const username = event.target.elements.username.value;
    const password1 = event.target.elements.password1.value;
    const password2 = event.target.elements.password2.value;

    if (password1 === password2) {
      this.props.onAuth(username, password1, password2);
    } else {
      errorMessage = "Passwords don't match"
    }
    this.props.history.push('/');
  }

  render() {

    let errorMessage = null;
    if (this.props.error) {
      errorMessage = (
        <p>{this.props.error.message}</p>
      );
    }

    return (
      <div>
        {errorMessage}
        {
          this.props.loading ?
            <Spinner animation="border" variant="info" />
            :
            <Form onSubmit={(event) => this.handleFormSubmit(event)}>

              <Form.Group>
                <Form.Label>Username</Form.Label>
                <Form.Control name="username" type="username" placeholder="Enter Username" />
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control name="password1" type="password" placeholder="Password" />
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control name="password2" type="password" placeholder="Confirm Password" />
              </Form.Group>

              <Button variant="primary" type="submit">
                Signup
              </Button>
              &nbsp; Or &nbsp;
              <NavLink to="/login">Login</NavLink>
            </Form>
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    loading: state.loading,
    error: state.error
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onAuth: (username, password1, password2) => dispatch(actions.authSignup(username, password1, password2))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignupForm);
