import React from 'react';

import TypingGame from './TypingGame';
import StartGame from '../components/StartGame';

import Button from 'react-bootstrap/Button';

class GameArea extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      phase: null
    };
  }
  
  componentDidMount() {

  }
  
  changePhase() {
    this.setState({
      phase: 1
    });
  }
  
  render() {
    return (
      <div>
        {
          this.state.phase === 0 ? 
            <div>
              <StartGame />
              <div className="gap-20"></div>
              <Button id="play-button" onClick={this.changePhase}>Play!</Button>
            </div>
          :
          <div></div>
        }
        
        {
          this.state.phase === 1 ?
            <div>
              <TypingGame />
            </div>
          :
          <div></div>
        }
      </div>
    );
  }
  
}

export default GameArea;
