import React from 'react';
import axios from 'axios';

import Form from 'react-bootstrap/Form';
import Score from '../components/GameComplete';

class TypingGame extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      fullText: [],
      displayText: [],
      score: 0,
      wordCounter: 0,
      textCounter: 0,
      input: ""
    };
  }

  componentDidMount() {
    axios.get('https://en.wikipedia.org/api/rest_v1/page/random/summary')
    .then(res => {
      const data = res.data.extract;
      // Sanitizing text by removing accents/diacritics
      const noSpacesText = data.replace(/\s\s+/g, ' ');
      let splitData = noSpacesText.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
      // Split text by sentence
      splitData = splitData.split(". ");
      console.log(splitData);
      this.setState(() => ({
        //One state variable for the full text and one for the first sentence
        fullText: splitData,
        displayText: splitData[0].split(" ")
      }));
    })
  }
  
  updateResponse = (event) => {
    this.setState({
      input: event.target.value
    })
  }

  nextWord = (event) => {
    if (event.code === 'Space') {
      const word = this.state.input;
      
      if (word.replace(" ", "") === this.state.displayText[this.state.wordCounter]) {
        this.setState({
          score: this.state.score + 1,
          input: "",
          wordCounter: this.state.wordCounter + 1
        });
      } else {
        this.setState({
          input: ""
        });
      }
      //check if we are done with the current text
      if (this.state.wordCounter + 1 === this.state.displayText.length) {
        //check if we are done with the whole text
        if (this.state.textCounter + 1 === this.state.fullText.length) {
          console.log("YOU WON!");
        } else {
          this.setState(state => ({
            displayText: state.fullText[state.textCounter + 1].split(" "),
            textCounter: state.textCounter + 1,
            wordCounter: 0
          }));
        } 
      }
    }
  }
  
  render() {
    return (
      <div className="typing-test">
        <div id="text-area">
          {this.state.displayText.map((text) =>
            <span className="test-displayText">{text} </span>
          )}
        </div>
        <div className="gap-20"></div>
        <Form.Control onKeyPress={this.nextWord} onChange={this.updateResponse} value={this.state.input} className="form"/>
        <div className="gap-20"></div>
        
        {/* Debugging info */}
        <div>Input: {this.state.input}</div>
        <div>Score: {this.state.score}</div>
        <div>Counter: {this.state.wordCounter}</div>
        <div>Current word: {this.state.displayText[this.state.wordCounter]}</div>
        
        <Score data={this.state.results} />
        
      </div>
    );
  }

}

export default TypingGame;
